sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	var start = g_form.getValue('start_date');
   //Type appropriate comment here, and begin script below
	var ga = new GlideAjax('LMS_Ajax'); //call Script Include to get leave dupration vlue
	ga.addParam('sysparm_name','getDateDiff'); // script include function
	ga.addParam('sysparm_start',start); // captured start date in variable
	ga.addParam('sysparm_end',newValue); // captured enddate=newValue to calculate difference

	ga.getXML(getDuration);

	function getDuration(response)
	{
		var answer = response.responseXML.documentElement.getAttribute("answer");
		var days = ( answer/(1000*60*60*24)); //miliseconds to days
		var days1 = days +1; //+1 to get correct leave days
		g_form.setValue('duration',days1); //setting value of leave days in Duration field.
	}

}
