sys_script_include.script:
var LMS_Ajax = Class.create();
LMS_Ajax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getDateDiff : function()
	{
		var d1 = new GlideDate();
		d1.setDisplayValue(this.getParameter('sysparm_start'));

		var d2 = new GlideDate();
		d2.setDisplayValue(this.getParameter('sysparm_end'));

		var duration = GlideDate.subtract(d1,d2); // diff between start and end date
		return duration.getNumericValue(); //convert duration value in Numeric form from date format
		//this script include connect with client script "setLeaveDuration" which converts this duration value(milliseconds)


	},

	//PART2
	//Populate Leave Details
	//Script Include: LMS_Ajax-->getLeaveBucket
	//Client Script:  Populate Leave Details
	getLeaveBucket : function()
	{
		var gr = new GlideRecord('x_475636_lms_leave_bucket');
		gr.addQuery('employee', this.getParameter('sysparm_user'));
		gr.addQuery('leave_type',this.getParameter('sysparm_leavetype'));
		gr.query();
		if(gr.next())
			{
				var leaveDetails = {}; // empty JSON Object
				leaveDetails.accrued = '' + gr.accrued;
				leaveDetails.balance = '' + gr.balance;
				leaveDetails.taken = '' + gr.taken;

				return JSON.stringify(leaveDetails); //stringify the JSON object to update data at once.

			}

	},

    type: 'LMS_Ajax'
});
