sys_script_include.script:
var HotelMngDateUtils = Class.create();
HotelMngDateUtils.prototype = {
    initialize: function() {
    },
getDateValidation: function(){
		var nowDate = new GlideDateTime();
	var fromDate = new GlideDateTime(current.start_date);
	var toDate = new GlideDateTime(current.end_date);
	if(fromDate.before(nowDate) || toDate.before(nowDate)){
		gs.addErrorMessage("Please choose a date which is not in Past");
		current.setAbortAction(true);
}
	if(fromDate.after(toDate)){
	gs.addErrorMessage("Please choose To date greater than From date");
		current.setAbortAction(true);
	}
	},

	//From and To Date Past Verification(BR) --> dateValidation(SI) --> HotelMngDateUtils(SI)

    type: 'HotelMngDateUtils'
};
